mapper.insert:
insert into webmember
(userid, firstname, userpwd, gender, lastname, birthday, phone, email, zipcode, country, state, city, address1, address2

,image


,hobby

)
values
(#{userid}, #{firstname}, #{userpwd}, #{gender},#{lastname}, #{birthday}, #{phone}, #{email}
, #{zipcode}, #{country}, #{state}, #{city}, #{address1}, #{address2}

,#{image}


,#{hobby}

)

mapper.select:
select * from webmember
where userid=#{userid}

and userpwd=#{userpwd}

select * from company
where co_id=#{co_id}

and co_pwd=#{co_pwd}

mapper.insert:
insert into company
(co_id, co_name,co_ceo_firstname, co_pwd, co_ceo_lastname, co_phone, co_email, co_website, co_zipcode, co_country, co_state, co_city
,co_address1, co_address2

, co_image

)
values
( #{co_id},#{co_name}, #{co_ceo_firstname}, #{co_pwd}, #{co_ceo_lastname}, #{co_phone}, #{co_email}, #{co_website}
,#{co_zipcode} , #{co_country}, #{co_state} , #{co_city}, #{co_address1}, #{co_address2}

, #{co_image}

)
